#==========================================================================# Shubham Chahal# 140020203052# Numerical Methods - Lab# Program to obtain solution of an equation using Euler's Method.#===========================================================================f=@(x,y)(x+sin(y))  x0=input('Enter the intial value of x = ');  y0=input('Enter the intial value of y = ');  xf=input('Enter the value of x at which value of y(final) is to be calculated= ');  n=input('Enter the number of sub-interval, we require = ');  t=cputime;   x=x0;  y=y0;  h=(xf-x0)/n;  fprintf('\n\n   S.No   x          y            Slope        \n\n');    k=0;  # Euler's Method Code    for  i=1:n             k++;        y=y+h*f(x,y);            x=x+h;            if(k<10)&&(k!=n)                fprintf("   %d.     %.2f      %.4f        %.4f\n",k,x,y,f(x,y));                elseif(k==n)&&(k<10)            fprintf("   %d.     %.2f      %.4f        \n",k,x,y);                elseif(k==n)&&(k>9)            fprintf("  %d.     %.2f      %.4f        \n",k,x,y);        else                fprintf("  %d.     %.2f      %.4f        %.4f\n",k,x,y,f(x,y));        end  end     fprintf('\nValue of y at x = %.3f is = %.4f',xf,y);    t=cputime-t;    fprintf('\n\nTotal Execution Time = %f\n\n',t);      