#==========================================================================# Shubham Chahal# 140020203052# Numerical Methods - Lab# Program to obtain solution of an equation using Runge-Kutta's Method.#==========================================================================  f =@(x,y)(x*y+y^2;)    x0=input('Enter the intial value of x = ');  y0=input('Enter the intial value of y = ');  xf=input('Enter the value of x at which value of y(final) is to be calculated= ');  n=input('Enter the number of sub-interval, we require = ');  t=cputime;   x=x0;  y=y0;  h=(xf-x0)/n;for i=1:n  k1=h*f(x,y);  k2=h*f(x+0.5*h , y+0.5*k1);  k3=h*f(x+0.5*h , y+0.5*k2);  k4=h*f(x+h , y+k3);  k=(1/6)*(k1+2*k2+2*k3+k4);  y=y+k;  x=x+h;  fprintf('y(%0.1f)=%f',x,y);  fprintf('\n');end